        self.outgoing_stack = []

    def enqueue(self, item):
        # Add item to incoming stack
        self.incoming_stack.append(item)

    def dequeue(self):
        if not self.is_empty():
            # Check if outgoing stack is empty, otherwise pop from it directly
            if len(self.outgoing_stack) > 0:
