% Uncomment one of the following to choose a specific level for ToC headings
% \documentclass[12pt]{report} % For multiple levels including sections, subsections, and subsubsections
% \documentclass[11pt]{book}    % For multiple levels including sections and subsections
% \documentclass{article}      % For single level headings

\usepackage{tocloft}          % Load package to customize appearance of ToC
\renewcommand{\cftchapfont}{\Large\bfseries}
\renewcommand{\cftsecfont}{\normalsize\bfseries}
\renewcommand{\cftsecpagefont}{\normalsize}
\renewcommand{\cftsecleader}{.}   % Change leader dot style if desired

\begin{document}

