```cpp
#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::ifstream inputFile; // Create an ifstream object to open the input file
    std::string fileName;   // A variable to store the filename as string
    std::string searchWord; // A variable to store the word to be searched for
    int count = 0;          // Initialize a counter to keep track of the word's occurrence

    std::cout << "Enter the filename: ";
    std::cin >> fileName;

    std::cout << "Enter the word you want to search for: ";
    std::cin >> searchWord;

    inputFile.open(fileName); // Open the file using the provided filename

    if (!inputFile) {       // Check whether the file was successfully opened or not
        std::cerr << "Unable to open the file." << std::endl;
        return -1;
    }

    while (std::string line; std::getline(inputFile, line)) { // Read each line from the file until end-of-file

        size_t pos = 0;
        while ((pos = line.find(searchWord, pos)) != std::string::npos) { // Find all instances of the search word within the current line
            count++;                              // Increment the word count
            pos += searchWord.length();             // Move past the found word
        }
    }

    inputFile.close(); // Close the file after processing its contents

    std::cout << "The word \"" << searchWord << "\" appears " << count << " times in the file.\n";

    return 0;
}
