class Queue:
    def __init__(self):
        self.enq_stack = []
        self.deq_stack = []

    def is_empty(self):
        return len(self.enq_stack) == 0 and len(self.deq_stack) == 0

    def enqueue(self, item):
        self.enq_stack.append(item)

    def dequeue(self):
