            raise Exception("Queue is empty")
        while not self.deq_stack or self.deq_stack[-1] != self.enq_stack[0]:
            self.deq_stack.append(self.enq_stack.pop())
        return self.deq_stack.pop()

    def peek(self):
        if self.is_empty():
            raise Exception("Queue is empty")
        while not self.deq_stack or self.deq_stack[-1] != self.enq_stack[0]:
            self.deq_stack.append(self.enq_stack.pop())
        return self.deq_stack[-1]

    def size(self):
        return len(self.enq_stack)
```

