const { Client, Guild, User } = require('discord.js');

module.exports = class Ban extends SlashCommandBuilder {
    constructor() {
        super("ban", "/ban @member");
    }

    async execute(client, interaction) {
        const member = interaction.options.getUser('member', true);
        await client.guilds.cache.get(interaction.guild_id).bans.add(member.id);

        await interaction.reply(`${member.username} has been banned from this server!`);
    }
};
```

