```C++
#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string fileName; // User-specified filename
    string targetWord; // Target word to search for
    int occurrenceCount = 0; // Count variable initialized to zero

    cout << "Enter filename: ";
    cin >> fileName;

    cout << "Enter target word: ";
    cin >> targetWord;

    // Create file stream object with error checking
    ifstream myFile(fileName);
    if (!myFile) {
        cerr << "Error opening file!" << endl;
        return 1;
    }

    // Loop through every line of file until end reached
    while (myFile.peek() != EOF) {
        string line;
        getline(myFile, line);

        size_t found = line.find(targetWord);
        while (found != string::npos) {
            occurrenceCount++;
            found = line.find(targetWord, found + 1);
        }
    }

