def lcs(str1, str2):
    m = len(str1)
    n = len(str2)

    # Initialize a table with zeros for storing the results
    LCS_table = [[0] * (n + 1) for _ in range(m + 1)]

    # Fill the bottom-left corner of the table with zeroes since an empty string matches any other string
    for I in range(1, m + 1):
