public boolean isPowerOfThree(int n) {
    // Check for invalid inputs
    if (n <= 0 || n > Integer.MAX_VALUE / 3) {
        throw new InputMismatchException("Input should be between 1 and 231");
    }

    int log3 = 0;
    while ((n >> log3) > 0) {
        log3++;
    }

